import React, { useRef, useEffect } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import './header.css';

const nav_links = [
  { path: '/home', display: 'Home' },
  { path: '/about', display: 'About' },
  { path: '/tours', display: 'Tours' },
];

const Header = () => {
  const headerRef = useRef(null);

  const stickyHeaderFunc = () => {
    if (window.scrollY > 80) {
      headerRef.current?.classList.add('sticky__header'); // Use optional chaining
    } else {
      headerRef.current?.classList.remove('sticky__header');
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', stickyHeaderFunc);

    // Cleanup function to remove the event listener on unmount
    return () => window.removeEventListener('scroll', stickyHeaderFunc);
  }, []); // Empty dependency array ensures this runs only once

  return (
    <header className="header" ref={headerRef}>
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            {/* Logo Section */}
            <div className="logo">
              <img src={logo} alt="Logo" />
            </div>
            {/* Navigation Menu */}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav_links.map((item, index) => (
                  <li className="nav_item" key={index}>
                    <NavLink
                      to={item.path}
                      className={({ isActive }) => (isActive ? 'active_link' : '')}
                    >
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>
            {/* Right Section with Buttons */}
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btns d-flex align-items-center gap-4">
                <Button tag={Link} to="/login" className="btn-login">
                  Login
                </Button>
                <Button tag={Link} to="/register" className="btn-register">
                  Register
                </Button>
              </div>
              <span className="mobile_menu">
                <i className="ri-menu-line"></i> {/* Ensure correct icon library is loaded */}
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
