import React, { useState } from 'react';
import { Container, Row, Col, Button, Card, CardBody, Collapse } from 'reactstrap';
import { Link } from 'react-router-dom';
import '../styles/admin-dashboard.css';

const AdminDashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  // Toggle the sidebar visibility
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <section className="admin-dashboard">
      <Container fluid>
        <Row>
          {/* Sidebar */}
          <Col
            lg="3"
            md="4"
            className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}
          >
            <div className="admin-menu">
              <h4 className="menu-title">Admin Panel</h4>
              <ul>
                <li><Link to="/admin/users">Manage Users</Link></li>
                <li><Link to="/admin/tours">Manage Tours</Link></li>
                <li><Link to="/admin/bookings">Bookings</Link></li>
                <li><Link to="/admin/settings">Settings</Link></li>
              </ul>
            </div>
          </Col>

          {/* Main Content */}
          <Col lg="9" md="8">
            {/* Toggle Button for Small Screens */}
            <Button
              className="btn toggle-sidebar-btn"
              onClick={toggleSidebar}
              style={{ display: 'none' }}  // Hide the button on large screens
            >
              {isSidebarOpen ? 'Hide Sidebar' : 'Show Sidebar'}
            </Button>

            <div className="dashboard-content">
              <h2 className="dashboard-title">Welcome, Admin</h2>

              {/* Dashboard Stats */}
              <Row>
                <Col lg="4" md="6">
                  <Card className="dashboard-card">
                    <CardBody>
                      <h3>150</h3>
                      <p>Total Users</p>
                    </CardBody>
                  </Card>
                </Col>
                <Col lg="4" md="6">
                  <Card className="dashboard-card">
                    <CardBody>
                      <h3>50</h3>
                      <p>Active Bookings</p>
                    </CardBody>
                  </Card>
                </Col>
                <Col lg="4" md="6">
                  <Card className="dashboard-card">
                    <CardBody>
                      <h3>20</h3>
                      <p>Available Tours</p>
                    </CardBody>
                  </Card>
                </Col>
              </Row>

              {/* Back to Home Button */}
              <Button className="btn primary__btn mt-4">
                <Link to="/home">Back to Home</Link>
              </Button>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default AdminDashboard;
