import React, { useRef, useEffect, useState } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import './header.css';

const nav_link = [
  { path: '/home', display: 'Home' },
  { path: '/about', display: 'About' },
  { path: '/tours', display: 'Tours' },
];

const Header = () => {
  const headerRef = useRef(null);
  const [lastScrollY, setLastScrollY] = useState(0); // Track last scroll position
  const [isVisible, setIsVisible] = useState(true); // Control visibility

  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY > lastScrollY && currentScrollY > 80) {
      setIsVisible(false); // Hide header on scroll down
    } else {
      setIsVisible(true); // Show header on scroll up
    }
    setLastScrollY(currentScrollY); // Update last scroll position
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll); // Clean up
    };
  }, [lastScrollY]);

  return (
    <header className={`header ${!isVisible ? 'header--hidden' : ''}`} ref={headerRef}>
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            {/* Logo */}
            <div className="logo">
              <img src={logo} alt="logo" />
            </div>

            {/* Menu */}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav_link.map((item, index) => (
                  <li className="nav_item" key={index}>
                    <NavLink
                      to={item.path}
                      className={(navClass) => (navClass.isActive ? 'active_link' : '')}
                    >
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>

            {/* Buttons */}
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btns d-flex align-items-center gap-4">
                <Button
                  tag={Link}
                  to="/login"
                  style={{ backgroundColor: 'white', color: 'black', border: 'none', fontWeight: 'bold' }}
                >
                  Login
                </Button>
                <Button
                  tag={Link}
                  to="/register"
                  style={{ backgroundColor: '#ff8c00', color: 'white', border: 'none', borderRadius: '18px' }}
                >
                  Register
                </Button>
              </div>
              <span className="mobile_menu">
                <i className="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
