import React, { useRef, useEffect, useState } from 'react';
import '../styles/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';

import calculateAvgRating from '../utils/avgRating';
import avatar from "../assets/images/avatar.jpg";
import Booking from '../components/Booking/Booking';
import Newsletter from '../shared/Newsletter';

import useFetch from '../hooks/useFetch';
import { BASE_URL } from '../utils/config';

const TourDetails = () => {
    const { id } = useParams();
    const reviewMsgRef = useRef('');
    const [tourRating, setTourRating] = useState(null);

    // Fetch tour data from backend
    const { data: tour, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);

    useEffect(() => {
        window.scrollTo(0, 0);
    }, [tour]);

    if (loading) {
        return <h4 className='text-center pt-5'>Loading...</h4>;
    }

    if (error) {
        return <h4 className='text-center pt-5'>{error}</h4>;
    }

    if (!tour) {
        return <h4 className='text-center pt-5'>Tour not found</h4>;
    }

    // Destructure tour properties
    const {
        photo,
        title,
        desc,
        price,
        address,
        reviews = [],
        city,
        distance,
        maxGroupSize,
    } = tour;

    const { totalRating, avgRating } = calculateAvgRating(reviews);

    const options = { day: "numeric", month: "long", year: "numeric" };

    // Submit review handler
    const submitHandler = (e) => {
        e.preventDefault();
        const reviewText = reviewMsgRef.current.value;

        console.log("Review Submitted: ", reviewText, "Rating:", tourRating);
        
        // API call to submit review can be added here

        // Reset review form
        reviewMsgRef.current.value = '';
        setTourRating(null);
    };

    return (
        <>
            <section>
                <Container>
                    <Row>
                        <Col lg='8'>
                            <div className="tour__content">
                                <img src={photo} alt={title} />
                                <div className="tour__info">
                                    <h2>{title}</h2>

                                    <div className="d-flex align-items-center gap-5">
                                        <span className='tour__rating d-flex align-items-center gap-1'>
                                            <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                                            {avgRating === 0 ? null : avgRating}
                                            {totalRating === 0 ? ' Not rated' : <span>({reviews.length})</span>}
                                        </span>

                                        <span>
                                            <i className="ri-map-pin-user-fill"></i> {address}
                                        </span>
                                    </div>

                                    <div className="tour__extra-details">
                                        <span><i className="ri-map-pin-2-line"></i> {city}</span>
                                        <span><i className="ri-money-dollar-circle-line"></i> ${price} / per person</span>
                                        <span><i className="ri-map-pin-time-line"></i> {distance} km</span>
                                        <span><i className="ri-group-line"></i> {maxGroupSize} people</span>
                                    </div>

                                    <h5>Description</h5>
                                    <p>{desc}</p>
                                </div>

                                {/* Tour Reviews Section */}
                                <div className="tour__reviews mt-4">
                                    <h4>Reviews ({reviews.length} reviews)</h4>
                                    <Form onSubmit={submitHandler}>
                                        <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                                            {[1, 2, 3, 4, 5].map((num) => (
                                                <span key={num} onClick={() => setTourRating(num)}>
                                                    {num} <i className="ri-star-s-fill"></i>
                                                </span>
                                            ))}
                                        </div>
                                        <div className="review__input">
                                            <input type='text'
                                                ref={reviewMsgRef}
                                                placeholder='Share your thoughts'
                                                required
                                            />
                                            <button className='btn primary__btn text-white' type='submit'>
                                                Submit
                                            </button>
                                        </div>
                                    </Form>

                                    <ListGroup className='user__reviews'>
                                        {reviews.map((review, index) => (
                                            <div key={index} className="review__item">
                                                <img src={avatar} alt='User Avatar' />

                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>{review.username || "Anonymous"}</h5>
                                                            <p>{new Date(review.date || "01-18-2024").toLocaleDateString("en-US", options)}</p>
                                                        </div>
                                                        <span className='d-flex align-items-center'>
                                                            {review.rating} <i className="ri-star-s-fill"></i>
                                                        </span>
                                                    </div>
                                                    <h6>{review.comment || "No comment"}</h6>
                                                </div>
                                            </div>
                                        ))}
                                    </ListGroup>
                                </div>
                            </div>
                        </Col>

                        <Col lg="4">
                            <Booking tour={tour} avgRating={avgRating} />
                        </Col>
                    </Row>
                </Container>
            </section>
            <Newsletter />
        </>
    );
};

export default TourDetails;
